/* <--- FUNCTIONS REACT ---> */
import React, { useState } from "react";
import { Link } from "react-router-dom";

/* <--- CSS ---> */
import styles from '../../Styles/Pages/Cadastro/Cadastro.module.css';

/* <--- COMPONENTES ---> */
import Footer from "../../Components/Footer/Footer";

/* <--- ASSETS ---> */
import Background from '../../Assets/Background.png';
import Logo from '../../Assets/Logo.png';

function Cadastro() {
    const [showPassword, setShowPassword] = useState(false);

    return (
        <>
            <section className={styles.sectionLogin}>
                <img src={Background} className={styles.Background} alt="Background" />
                
                <Link to='/'>
                    <img src={Logo} className={styles.Logo} alt="Logo" />
                </Link>

                <div className={styles.RegisterContainer}>
                    <h2 className={styles.Title}>
                        Faça seu <span>&nbsp;cadastro</span>
                    </h2>
                    <p className={styles.SubTitle}>Boas vindas!</p>

                    {/* Inputs do formulário */}
                    <input type="text" placeholder="Seu nome" className={styles.Input} />
                    <input type="email" placeholder="Seu e-mail" className={styles.Input} />
                    <input type={showPassword ? "text" : "password"} placeholder="Digite a senha" className={styles.Input} />
                    <input type={showPassword ? "text" : "password"} placeholder="Confirme a senha" className={styles.Input} />

                    {/* Opções extras */}
                    <div className={styles.Options}>
                        <label className={styles.checkboxLabel}>
                            <input type="checkbox" onChange={() => setShowPassword(!showPassword)} />
                            <p>Mostrar Senha</p>
                        </label>
                    </div>
                    <button className={styles.button}>Cadastre-se</button>
                </div>

                <div className={styles.ContainerNotRegister}>
                    <h2 className={styles.Titulo}>
                        Já tem <span>&nbsp;cadastro?</span>
                    </h2>
                    <p className={styles.SubTitulo}>Faça seu login!</p>
                    <Link to="/login">
                        <button className={styles.Btn_Register}>Faça login</button>
                    </Link>
                </div>

                <Footer />
            </section>
        </>
    );
}

export default Cadastro;
