/* <--- FUNCTIONS REACT ---> */
import React, { useState } from "react";
import { Link } from "react-router-dom";

/* <--- CSS ---> */
import styles from '../../Styles/Pages/Cadastro/Cadastro.module.css';

/* <--- COMPONENTES ---> */
import Footer from "../../Components/Footer/Footer";

/* <--- ASSETS ---> */
import Background from '../../Assets/Background.png';
import Logo from '../../Assets/Logo.png';

function Cadastro() {
    const [showPassword, setShowPassword] = useState(false);
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");

    const handleCadastro = () => {
        if (password !== confirmPassword) {
            setErrorMessage("As senhas não coincidem!");
        } else {
            setErrorMessage("");
            alert("Cadastro realizado com sucesso!");
            // Aqui você pode adicionar a lógica para enviar os dados ao backend
        }
    };

    return (
        <>
            <section className={styles.sectionLogin}>
                <img src={Background} className={styles.Background} alt="Background" />

                <Link to='/'>
                    <img src={Logo} className={styles.Logo} alt="Logo" />
                </Link>

                <div className={styles.RegisterContainer}>
                    <h2 className={styles.Title}>
                        Faça seu <span>&nbsp;cadastro</span>
                    </h2>
                    <p className={styles.SubTitle}>Boas vindas ao To-Do List</p>

                    <input type="text" placeholder="Seu nome" className={styles.Input} />
                    <input type="email" placeholder="Seu e-mail" className={styles.Input} />

                    <input
                        type={showPassword ? "text" : "password"}
                        placeholder="Digite a senha"
                        className={styles.Input}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />

                    <input
                        type={showPassword ? "text" : "password"}
                        placeholder="Confirme a senha"
                        className={styles.Input}
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                    />

                    <div className={styles.Options}>
                        <label className={styles.checkboxLabel}>
                            <input type="checkbox" onChange={() => setShowPassword(!showPassword)} />
                            <p>Mostrar Senha</p>
                        </label>
                    </div>

                    {/* Exibe a mensagem de erro caso as senhas não sejam iguais */}
                    {errorMessage && <p className={styles.ErrorMessage}>{errorMessage}</p>}

                    <button className={styles.button} onClick={handleCadastro}>
                        Cadastre-se
                    </button>
                </div>

                <div className={styles.ContainerNotRegister}>
                    <h2 className={styles.Titulo}>
                        Já tem <span>&nbsp;cadastro?</span>
                    </h2>
                    <p className={styles.SubTitulo}>Faça seu login!</p>
                    <Link to="/login">
                        <button className={styles.Btn_Register}>Faça login</button>
                    </Link>
                </div>

                <Footer />
            </section>
        </>
    );
}

export default Cadastro;
