/* <--- FUNCTIONS REACT ---> */
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

/* <--- PAGES ---> */
import Home from "./Pages/Home/Home";
import Login from "./Pages/Login/Login";
import Cadastro from "./Pages/Cadatsro/Cadastro";

function App() {

  const [loading, setLoading] = useState(false);
  const location = useLocation();

  useEffect(() => {
    setLoading(true); // Ativa a tela de carregamento
    const timeout = setTimeout(() => setLoading(false), 1000); // Simula um delay (1s)

    return () => clearTimeout(timeout); // Limpa o timeout ao desmontar
  }, [location.pathname]);

  return (
    <Router>
      {loading && <LoadingScreen />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/cadastro" element={<Cadastro />} />
      </Routes>
    </Router>
  );
}

export default App;
