import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import React, { useRef, useState, useEffect } from "react";
import styles from '../../Styles/Components/List/ToDoList.module.css';

import AdicionarNovaTarefa from '../../Assets/Icons/Add.svg';

function TodoList() {
  const [columns, setColumns] = useState({
    toDo: {
      name: "A Fazer",
      items: [{ id: "1", content: "Comprar leite" }],
    },
    inProgress: {
      name: "Em Andamento",
      items: [],
    },
    done: {
      name: "Concluído",
      items: [],
    },
  });
  return (
    <>
      <section className={styles.SectionTarefas}>
        <section className={styles.Tarefas}>
          <div className={styles.NomeSection}>
            <h1>
              A fazer
            </h1>
            <span>
              0
            </span>
          </div>
            <img src={AdicionarNovaTarefa} className={styles.AddButton} />
        </section>
        <section className={styles.Tarefas}>
          <div className={styles.NomeSection}>
            <h1>
              Em Andamento
            </h1>
            <span>
              0
            </span>
          </div>
            <img src={AdicionarNovaTarefa} className={styles.AddButton} />
        </section>
        <section className={styles.Tarefas}>
          <div className={styles.NomeSection}>
            <h1>
              Concluído
            </h1>
            <span>
              0
            </span>
          </div>
            <img src={AdicionarNovaTarefa} className={styles.AddButton} />
        </section>
      </section>
    </>
  );
}

export default TodoList;
