import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import React, { useRef, useState, useEffect } from "react";
import styles from '../../Styles/Components/List/ToDoList.module.css';

 function TodoList() {


  return (
    <div className={styles.Pai}>
      <DragDropContext onDragEnd={handleDragEnd}>
        {Object.entries(columns).map(([columnId, column]) => (
          <div key={columnId} className={styles.Sections}>
            <div className={styles.SectionsName}>
              <h2>{column.name} ({column.items.length})</h2>
              <button onClick={() => handleAddTask(columnId)} className="add-button">+</button>
            </div>
            <Droppable droppableId={columnId}>
              {(provided) => (
                <div
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                  className="droppable-area"
                >
                  {column.items.map((item, index) => (
                    <Draggable key={item.id} draggableId={item.id} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="task"
                        >
                          {item.content}
                          <button onClick={() => handleRemoveTask(columnId, item.id)} className="remove-button">x</button>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>
        ))}
      </DragDropContext>
    </div>
  );
}
