import React, { useState, useEffect } from "react";
import styles from "../../.module.css";

const TaskHistory = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    // Simulando dados (depois voc√™ pode conectar com um banco de dados)
    const fetchTasks = async () => {
      const data = [
        { id: 1, name: "Projeto To-Do List", date: "16/01/2025 √†s 19:20", status: "todo" },
        { id: 2, name: "Projeto Back-End", date: "18/01/2025 √†s 12:55", status: "todo" }
      ];
      setTasks(data);
    };

    fetchTasks();
  }, []);

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h2>üìå Hist√≥rico</h2>
        <input type="text" placeholder="üîç Pesquise" className={styles.search} />
      </header>

      <table className={styles.historyTable}>
        <thead>
          <tr>
            <th>Nome da Tarefa</th>
            <th className={styles.statusHeader}>A Fazer</th>
            <th className={styles.statusHeader}>Em Andamento</th>
            <th className={styles.statusHeader}>Conclu√≠do</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.name}</td>
              <td className={styles.todo}>{task.status === "todo" ? task.date : ""}</td>
              <td className={styles.inProgress}>
                {task.status === "inProgress" ? "Em progresso..." : "O usu√°rio ainda n√£o avan√ßou a tarefa"}
              </td>
              <td className={styles.completed}>
                {task.status === "completed" ? "Finalizado!" : "O usu√°rio ainda n√£o avan√ßou a tarefa"}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TaskHistory;
