/* <--- FUNCTIONS REACT ---> */
import React, { useRef, useState, useEffect } from "react";
import { Link } from "react-router-dom";

/* <--- CSS ---> */
import styles from '../../Styles/Pages/Login/Login.module.css';

/* <--- COMPONENTES ---> */
import Footer from "../../Components/Footer/Footer";

/* <--- ASSETS ---> */
import Background from '../../Assets/Background.png';
import Logo from '../../Assets/Logo.png';

function Login() {

    const [showPassword, setShowPassword] = useState(false);

    return (
        <>
            <section className={styles.sectionLogin}>
                <img src={Background} className={styles.Background} />
                <Link to='/'>
                    <img src={Logo} className={styles.Logo} />
                </Link>

                <div className={styles.container}>
                    <div className={styles.card}>
                        <h2 className={styles.title}>
                            Faça seu <span className={styles.highlight}>login</span>
                        </h2>
                        <p className={styles.subtitle}>Boas vindas</p>

                        <input type="text" placeholder="Seu nome" className={styles.input} />
                        <input
                            type={showPassword ? "text" : "password"}
                            placeholder="Digite a senha"
                            className={styles.input}
                        />

                        <div className={styles.options}>
                            <label className={styles.checkboxLabel}>
                                <input
                                    type="checkbox"
                                    onChange={() => setShowPassword(!showPassword)}
                                />
                                Mostrar Senha
                            </label>
                            <a href="#" className={styles.forgotPassword}>
                                Esqueceu a senha?
                            </a>
                        </div>

                        <button className={styles.button}>Faça login</button>
                    </div>
                </div>
            </section>
            <Footer />
        </>
    );
}

export default Login;
